export default function MainAdSelector() {
  const [selectedAd, setSelectedAd] = useState<string | null>(null);
  const [selectedFile, setSelectedFile] = useState<string | null>(null);


  const renderIcon = (type: string) => {
    switch (type) {
      case "html5":
        return <FaFileCode className="text-blue-500 text-2xl" />;
      case "video":
        return <FaFileVideo className="text-green-500 text-2xl" />;
      case "image":
        return <FaFileImage className="text-blue-500 text-2xl" />;
      default:
        return null;
    }
  };

  return (
    <div className="flex flex-col items-center p-8">
      <div className="grid grid-cols-3 gap-4">
        {files.map((file) => (
          <div
            key={file.name}
            className="p-4 border rounded-lg cursor-pointer hover:shadow-lg"
            onClick={() => {
              setSelectedAd(file.type);
              setSelectedFile(file.url);
            }}
          >
            <Image 
              src={`data:image/jpeg;base64,${file.thumbnail}`}
              alt={file.name} 
              width={200}
              height={128} 
              className="w-full h-32 object-cover mb-2" 
            />
            <div className="flex items-center gap-2">
              {renderIcon(file.type)}
              <p className="font-semibold">{file.name}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="mt-8 w-full">
        {selectedAd === "html5" && selectedFile && <Html5Ad url={selectedFile} />}
        {selectedAd === "video" && selectedFile && <VideoAd url={selectedFile} />}
        {selectedAd === "image" && selectedFile && <ImageAd url={selectedFile} />}
      </div>
    </div>
  );
}
